import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

import Typewriter from 'typewriter-effect';

const header1 = 'Your passwords aren\'t as secure as you think they are.'
// const header2 = 'as you think they are.'
const subheader1 = 'However, we can fix that.'

function TypewriterComponent (props) {
    return (
        <Typewriter
            options={{
                delay: 50,
            }}
            onInit={(typewriter) => {
                typewriter
                    .pauseFor(props.delay)
                    .typeString(props.string)
                    .start();
            }}
        />
    )
    //return <h1>{props.header}</h1>       
}

/*function TypewriterComponent (props) {
    return ( 
        <Typewriter
            options={{
