                typewriter
                    .pauseFor(2500)
                    .typeString('<h1>However, we can fix that.<h1>')
                    .start();
            }}
        />
    )
} */


function App () {
    return (
        <div>
            <div class='OpeningHeader'>
                <TypewriterComponent string={header1} delay='0'  />
            </div>
            <div class='SubHeader'>
                <TypewriterComponent string={subheader1} delay='2500' />
            </div>
        </div>
        )
    }

ReactDOM.render(
    <App />,
    document.getElementById('app')

)
