import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

import Typewriter from 'typewriter-effect';

const header1 = 'Your passwords aren\'t as secure'
const header2 = 'as you think they are.'
const subheader1 = 'However, we can fix that.'

function HeaderComponent (props) {
    /*return (
        <Typewriter
            options={{
                loop: true,
            }}
            onInit={(typewriter) => {
                typewriter
                    .typeString('<h1>Your passwords aren\'t as secure as you think they are.<h1>')
                    .start();
            }}
        />
    ) */
    return <h1>{props.header}</h1>       
}

function TypewriterComponent (props) {
    return ( 
        <Typewriter
            options={{
                strings: [props.type],
                autoStart: true,
                loop: true,
            }}
        />
    )
}

function SubHeaderComponent () {
    return (
        <Typewriter
            onInit={(typewriter) => {
                typewriter
                    .pauseFor(2500)
                    .typeString('<h1>However, we can fix that.<h1>')
                    .start();
            }}
        />
    )
}


function App () {
    return (
        <div>
            <div id='OpeningHeader'>
                <HeaderComponent header={header1} />
                <TypewriterComponent type={header1} />
            </div>
            <div class='text'>
                <SubHeaderComponent sub={subheader1} />
            </div>
        </div>
        )
    }

ReactDOM.render(
    <App />,
    document.getElementById('app')

)
