import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import zxcvbn from 'zxcvbn'

import Typewriter from 'typewriter-effect';

const header1 = 'Your passwords aren\'t as secure as you think they are.'
// const header2 = 'as you think they are.'
const subheader1 = ' However,'
const subheader2 = ' we can fix that.'

function TypewriterComponent (props) {
    return (
        <Typewriter
            options={{
                delay: 60,
            }}
            onInit={(typewriter) => {
                typewriter
                    .pauseFor(1000)
                    .typeString(props.string1)
                    .pauseFor(props.delay1)
                    .typeString(props.string2)
                    .pauseFor(props.delay2)
                    .typeString(props.string3)
                    .start();
            }}
        />
    )
}

class App extends React.Component {
    state = {
        password: "",
        suggestions: []
    }

    onPassChange = (e) => {
        const password = e.target.value
        const evaluation = zxcvbn(password)
        console.log(evaluation)
        this.setState({
            password,
            score: evaluation.score,
            suggestions: evaluation.feedback.suggestions,
        })
    }


    render() {
        const {score, suggestions} = this.state
        return (
            <div class='Container'>
                    <div class='OpeningHeader'>
                        <TypewriterComponent 
                            string1={header1} 
                            delay1='1500' 
                            string2={subheader1} 
                            delay2='1000' 
                            string3={subheader2}  
                        />
                    </div>
                    <div class='Continue'>
                        Continue to learn about how to make a strong password, test passwords, and learn about password cracking
                    </div>
                    <div class='DictionaryCheck'>
                        <input
                            type='text'
                            value={this.state.password}
                            placeholder="password..."
                            onChange={this.onPassChange}
                        /> {score}
                        <ul>
                            {suggestions.map((s, index) =>
                                <li key={index}>{s}</li>
                            )}
                        </ul>
                    </div>
            </div>
            );
    }
}

ReactDOM.render(
    <App />,
    document.getElementById('app')

)
